

<main class="flex h-screen pl-[20rem] flex-col max-[820px]:pl-[13rem] max-[637px]:pl-[7rem] max-[537px]:pl-[3rem] max-[383px]:pl-[2rem]">
    <form class="flex flex-col" action="">
        <label for="movieInput" class="uppercase text-[2.2rem] pl-[5rem] pt-[2rem] max-[707px]:text-[1.8rem] max-[462px]:text-[1.2rem]">Movies</label>
        <input id="movieInput" class="w-[24rem] h-[3rem] bg-lightg rounded-lg p-2p-2 ml-[5rem] max-[707px]:w-[20rem] max-[707px]:h-[2rem] max-[462px]:w-[15rem]" type="text" placeholder="Movie title here">
        <label for="imageInput" class="uppercase text-[2.2rem] pl-[5rem] pt-[1rem] max-[707px]:text-[1.8rem] max-[462px]:text-[1.2rem]">Image</label>
        <input id="imageInput" class="w-[24rem] h-[3rem] bg-lightg rounded-lg p-2 ml-[5rem] max-[707px]:w-[20rem] max-[707px]:h-[2rem] max-[462px]:w-[15rem]" type="text" placeholder="Image link here">
        <label for="infoInput" class="uppercase text-[2.2rem] pl-[5rem] pt-[1rem] max-[707px]:text-[1.8rem] max-[462px]:text-[1.2rem]">Description</label>
        <textarea id="infoInput" class="w-[24rem] h-[7rem] bg-lightg rounded-lg  ml-[5rem] p-2 max-[707px]:w-[20rem] max-[462px]:w-[15rem]" placeholder="Info here"></textarea>
        <label for="lengthInput" class="uppercase text-[2.2rem] pl-[5rem] pt-[1rem] max-[707px]:text-[1.8rem] max-[462px]:text-[1.2rem]">Lenght</label>
        <input id="lenghtInput" class="w-[24rem] h-[3rem] bg-lightg rounded-lg p-2 ml-[5rem] max-[707px]:w-[20rem] max-[707px]:h-[2rem] max-[462px]:w-[15rem]" type="text" placeholder="Lenght here">
        <label for="releaseInput" class="uppercase text-[2.2rem] pl-[5rem] pt-[1rem] max-[707px]:text-[1.8rem] max-[462px]:text-[1.2rem]">Realese date</label>
        <input id="releaseInput" class="w-[24rem] h-[3rem] bg-lightg rounded-lg p-2 ml-[5rem] max-[707px]:w-[20rem] max-[707px]:h-[2rem] max-[462px]:w-[15rem]" type="text" placeholder="Release date">
        <label for="genreInput" class="uppercase text-[2.2rem] pl-[5rem] pt-[1rem] max-[707px]:text-[1.8rem] max-[462px]:text-[1.2rem]">Genre</label>
        <input id="genreInput" class="w-[24rem] h-[3rem] bg-lightg rounded-lg p-2 ml-[5rem] max-[707px]:w-[20rem] max-[707px]:h-[2rem] max-[462px]:w-[15rem]" type="text" placeholder="Genre">
        <label for="countryInput" class="uppercase text-[2.2rem] pl-[5rem] pt-[1rem] max-[707px]:text-[1.8rem] max-[462px]:text-[1.2rem]">Country</label>
        <input id="countryInput" class="w-[24rem] h-[3rem] bg-lightg rounded-lg p-2 ml-[5rem] max-[707px]:w-[20rem] max-[707px]:h-[2rem] max-[462px]:w-[15rem]" type="text" placeholder="Country">
        <label for="imdbInput" class="uppercase text-[2.2rem] pl-[5rem] pt-[1rem] max-[707px]:text-[1.8rem] max-[462px]:text-[1.2rem]">Imdb</label>
        <input id="imdbInput" class="w-[24rem] h-[3rem] bg-lightg rounded-lg p-2 ml-[5rem] max-[707px]:w-[20rem] max-[707px]:h-[2rem] max-[462px]:w-[15rem]" type="text" placeholder="Imdb">
        <label for="directorInput" class="uppercase text-[2.2rem] pl-[5rem] pt-[1rem] max-[707px]:text-[1.8rem] max-[462px]:text-[1.2rem]">Director</label>
        <input id="directorInput" class="w-[24rem] h-[3rem] bg-lightg rounded-lg p-2 ml-[5rem] max-[707px]:w-[20rem] max-[707px]:h-[2rem] max-[462px]:w-[15rem]" type="text" placeholder="Director">
        <button type="submit" class="bg-tree rounded-xl w-[18rem] h-[3rem] text-white ml-[5rem] text-2xl hover:font-bold hover:bg-green mt-[3rem] mb-[3rem] max-[707px]:w-[13rem] max-[707px]:text-xl max-[707px]:h-[2.5rem] max-[462px]:w-[9rem]">SUBMIT</button>
    </form>
</main>
